import React, { useState, useEffect, useRef } from 'react';
import './App.css';

// grade 배열을 상단으로 이동하고 export
export const grade = ['SSR', 'SR', 'S', 'R', 'H', 'N'];
const jobs = ['전사', '마법사', '궁수'];

// 적 파티 이름 생성을 위한 배열들
const enemyPrefixes = ['암흑', '혼돈', '악마', '지옥', '광기', '심연', '그림자', '악령', '죽음', '공포'];
const enemySuffixes = ['군단', '부대', '군대', '무리', '집단', '연합', '길드', '클랜', '파티', '팀'];

// 카드 등급에 따른 확률 계산 함수
function getLuck() {
  var r = Math.floor(Math.random() * 100) + 1;  // 1~100 사이의 랜덤 숫자 생성
  var t = 5;	// 기본값: N Normal

  // 확률에 따라 등급을 결정
  if (r <= 50) {	// 확률에 따라 등급 결정
    t = 4; // H High
  }
  if (r <= 20) {	// 확률에 따라 등급 결정
    t = 3; // R Rare
  }
  if (r <= 10) {	// 확률에 따라 등급 결정
    t = 2; // S Super
  }
  if (r <= 4) {	// 확률에 따라 등급 결정
    t = 1; // SR SuperRare
  }
  if (r === 1) {	// 1% 확률로 SSR
    t = 0; // SSR SuperSuperRare
  }

  return t;  // 등급 반환
}

// 주사위처럼 숫자 범위 내에서 랜덤 값을 반환하는 함수
function dice(s, e) {
  return Math.floor(Math.random() * (e - s + 1)) + s;
}

// Card 컴포넌트 - 각 카드의 UI를 담당
function Card({ job, grade, isFlipped, onFlip, draggable, onDragStart, onDragOver, onDrop, index }) {
  const [rotation, setRotation] = useState({ x: 0, y: 0 });
  const cardRef = useRef(null);

  // 카드의 회전 효과를 위한 마우스 이동 이벤트 처리
  const handleMouseMove = (e) => {
    if (!cardRef.current) return;

    const card = cardRef.current;
    const rect = card.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const centerX = rect.width / 2;
    const centerY = rect.height / 2;

    const rotateX = -(y - centerY) / 10;
    const rotateY = (x - centerX) / 10;

    setRotation({ x: rotateX, y: rotateY });
  };

  const handleMouseLeave = () => {
    setRotation({ x: 0, y: 0 });
  };

  return (
    <div 
      ref={cardRef}
      className={`card ${job} ${grade} ${isFlipped ? 'flipped' : ''}`}
      style={{
        transform: `perspective(1000px) rotateX(${rotation.x}deg) rotateY(${rotation.y}deg) ${isFlipped ? 'rotateY(180deg)' : ''}`,
        transition: 'transform 0.6s'
      }}
      onClick={onFlip}  // 카드 클릭 시 flip 상태 변경
      onMouseMove={handleMouseMove}  // 카드 위에서 마우스 이동 시 회전
      onMouseLeave={handleMouseLeave}  // 카드에서 마우스 나가면 회전 초기화
      draggable={draggable}  // 카드 드래그 가능 여부
      onDragStart={(e) => onDragStart && onDragStart(e, index)}  // 드래그 시작 시 처리
      onDragOver={(e) => onDragOver && onDragOver(e)}  // 드래그 오버 시 처리
      onDrop={(e) => onDrop && onDrop(e, index)}  // 드롭 시 처리
    >
      <div className="card-front">
        <div className="card-content">
          <div className="card-title">{job}</div>  {/* 직업 표시 */}
          <div className="card-grade">{grade}</div>  {/* 카드 등급 표시 */}
        </div>
      </div>
      <div className="card-back">
        <div className="card-back-design"></div>  {/* 카드 뒷면 디자인 */}
      </div>
    </div>
  );
}

// 카드들을 묶어서 보여주는 영역 (드래그 앤 드롭 처리)
function CardArea({ children, onDragOver, onDrop, type }) {
  return (
    <div 
      id='card_area'
      className={`card-area ${type}`}
      onDragOver={(e) => onDragOver && onDragOver(e)}  // 드래그 오버 처리
      onDrop={(e) => onDrop && onDrop(e)}  // 드롭 처리
    >
      {children}
    </div>
  );
}

// 전투 컴포넌트 - 실제 전투가 이루어지는 영역
function Battle({ 
  playerParty, 
  enemyParty, 
  playerPartyName, 
  enemyPartyName,
}) {
  const [isBattling, setIsBattling] = useState(false);
  const [battleLog, setBattleLog] = useState([]);
  const logContainerRef = useRef(null);

  // 로그 자동 스크롤
  useEffect(() => {
    if (logContainerRef.current) {
      logContainerRef.current.scrollTop = logContainerRef.current.scrollHeight;
    }
  }, [battleLog]);  // battleLog가 업데이트 될 때마다 스크롤을 최신 상태로 이동

  // 카드의 스탯 계산 함수 - 카드의 직업과 등급에 따라 다르게 계산
  const getCardStats = (card) => {
    const baseStats = {
      SSR: { hp: 100, atk: 20, def: 15 },
      SR: { hp: 85, atk: 17, def: 13 },
      S: { hp: 70, atk: 15, def: 11 },
      R: { hp: 60, atk: 13, def: 9 },
      H: { hp: 50, atk: 11, def: 7 },
      N: { hp: 40, atk: 9, def: 5 }
    };

    const jobMultipliers = {
      전사: { hp: 1.2, atk: 0.9, def: 1.3 },
      마법사: { hp: 0.8, atk: 1.4, def: 0.7 },
      궁수: { hp: 0.9, atk: 1.2, def: 0.8 },
      도적: { hp: 0.7, atk: 1.3, def: 0.6 },
      사제: { hp: 1.1, atk: 0.7, def: 1.0 }
    };

    const base = baseStats[card.grade];  // 기본 스탯을 가져옴
    const multiplier = jobMultipliers[card.job];  // 직업에 따른 배수 값을 가져옴

    return {
      hp: Math.floor(base.hp * multiplier.hp),
      atk: Math.floor(base.atk * multiplier.atk),
      def: Math.floor(base.def * multiplier.def),
      maxHp: Math.floor(base.hp * multiplier.hp)  // 최대 HP
    };
  };

  // 데미지 계산 함수 - 공격자와 수비자의 스탯에 따른 데미지 계산
  const calculateDamage = (attacker, defender) => {
    const isCritical = Math.random() < 0.15;  // 15% 확률로 크리티컬
    const isEvaded = Math.random() < 0.10;  // 10% 확률로 회피

    if (isEvaded) {
      return { damage: 0, isCritical: false, isEvaded: true };  // 회피 시 데미지는 0
    }

    let damage = Math.max(1, attacker.atk - defender.def);  // 최소 1의 데미지를 보장
    if (isCritical) {
      damage = Math.floor(damage * 1.5);  // 크리티컬 시 데미지 1.5배
    }

    return { damage, isCritical, isEvaded: false };
  };

  // 로그 메시지 생성 함수 - 전투 로그에 출력할 메시지 형식화
  const createLogMessage = (attacker, target, damageInfo, isPlayerAttack) => {
    const attackerName = isPlayerAttack ? playerPartyName : enemyPartyName;
    const targetName = isPlayerAttack ? enemyPartyName : playerPartyName;
    
    if (damageInfo.isEvaded) {
      return {
        text: `${targetName}의 ${target.job}(${target.grade})가 ${attackerName}의 ${attacker.job} 공격을 회피했습니다!`
      };
    }

    const damageText = damageInfo.isCritical 
      ? `${damageInfo.damage}의 크리티컬 데미지!` 
      : `${damageInfo.damage}의 데미지!`;

    return {
      text: `${attackerName}의 ${attacker.job}(${attacker.grade})가 ${targetName}의 ${target.job}(${target.grade})에게 ${damageText}`
    };
  };

  // 전투 진행 함수 - 실제 전투를 진행하고 로그 업데이트
  const startBattle = () => {
    setIsBattling(true);
    setBattleLog([]);
    
    let playerPartyClone = [...playerParty];
    let enemyPartyClone = [...enemyParty];

    // 간단한 턴제 전투 진행
    let turn = 0;
    const interval = setInterval(() => {
      const isPlayerTurn = turn % 2 === 0;

      const attacker = isPlayerTurn
        ? playerPartyClone[Math.floor(Math.random() * playerPartyClone.length)]
        : enemyPartyClone[Math.floor(Math.random() * enemyPartyClone.length)];
      const defender = isPlayerTurn
        ? enemyPartyClone[Math.floor(Math.random() * enemyPartyClone.length)]
        : playerPartyClone[Math.floor(Math.random() * playerPartyClone.length)];

      const attackerStats = getCardStats(attacker);
      const defenderStats = getCardStats(defender);

      const damageInfo = calculateDamage(attackerStats, defenderStats);

      // 로그 추가
      setBattleLog((prevLog) => [
        ...prevLog,
        createLogMessage(attacker, defender, damageInfo, isPlayerTurn)
      ]);

      // 턴 진행
      if (playerPartyClone.length === 0 || enemyPartyClone.length === 0) {
        clearInterval(interval);  // 전투가 끝나면 타이머 중지
      }

      turn++;
    }, 1500);  // 1.5초마다 턴 진행
  };

  return (
    <div>
      <button onClick={startBattle} disabled={isBattling}>전투 시작</button>
      <div className="battle-log" ref={logContainerRef}>
        {battleLog.map((log, idx) => (
          <div key={idx}>{log.text}</div>
        ))}
      </div>
    </div>
  );
}

// App 컴포넌트 - 전체 애플리케이션의 UI를 담당
function App() {
  const [cards, setCards] = useState([]);
  const [playerParty, setPlayerParty] = useState([]);
  const [enemyParty, setEnemyParty] = useState([]);

  const generateCard = () => {
    const job = jobs[dice(0, jobs.length - 1)];	// 랜덤 직업 선택
    const gradeIdx = getLuck();  // 카드 등급 랜덤 선택
    const grade = grade[gradeIdx];
    const newCard = { job, grade };

    setCards([...cards, newCard]);  // 새로운 카드 추가
  };

  const handleCardDragStart = (e, index) => {
    e.dataTransfer.setData('cardIndex', index);  // 카드 드래그 시작 시 index 저장
  };

  const handleCardDragOver = (e) => {
    e.preventDefault();  // 드래그 오버 시 기본 동작 방지
  };

  const handleCardDrop = (e, targetIndex) => {
    const sourceIndex = e.dataTransfer.getData('cardIndex');
    const newCards = [...cards];
    const movedCard = newCards[sourceIndex];

    // 카드 위치 변경
    newCards.splice(sourceIndex, 1);
    newCards.splice(targetIndex, 0, movedCard);

    setCards(newCards);  // 카드 상태 업데이트
  };

  return (
    <div className="App">
      <h1>카드 전투</h1>
      <div className="generate-card">
        <button onClick={generateCard}>카드 생성</button>
      </div>

      <CardArea onDragOver={handleCardDragOver} onDrop={handleCardDrop} type="player">
        {cards.map((card, index) => (
          <Card
            key={index}
            job={card.job}
            grade={card.grade}
            isFlipped={false}
            onFlip={() => {}}
            draggable
            onDragStart={(e) => handleCardDragStart(e, index)}
            onDragOver={handleCardDragOver}
            onDrop={(e) => handleCardDrop(e, index)}
            index={index}
          />
        ))}
      </CardArea>

      {/* 전투 시작 컴포넌트 */}
      <Battle
        playerParty={playerParty}
        enemyParty={enemyParty}
        playerPartyName="플레이어"
        enemyPartyName="적군"
      />
    </div>
  );
}

export default App;
