import React, { useState } from 'react';
import './App.css';

function App() {
  const [my, setMy] = useState([]); // 보유 카드 목록
  const [party, setParty] = useState([]); // 파티 멤버 목록
  const [draggedCard, setDraggedCard] = useState(null);

  // 드래그 시작
  const handleDragStart = (e, card, source) => {
    e.dataTransfer.setData('card', JSON.stringify(card));
    e.dataTransfer.setData('source', source);
    setDraggedCard(card);
  };

  // 드래그 오버
  const handleDragOver = (e) => {
    e.preventDefault();
    if (e.target.className.includes('drop-area')) {
      e.target.classList.add('drag-over');
    }
  };

  // 드래그 떠날 때
  const handleDragLeave = (e) => {
    if (e.target.className.includes('drop-area')) {
      e.target.classList.remove('drag-over');
    }
  };

  // 드롭
  const handleDrop = (e, destination) => {
    e.preventDefault();
    const card = JSON.parse(e.dataTransfer.getData('card'));
    const source = e.dataTransfer.getData('source');

    if (source === destination) return;

    if (destination === 'party') {
      if (party.length >= 5) {
        alert('파티 인원은 최대 5명입니다!');
        return;
      }
      setParty(prev => [...prev, card]);
      setMy(prev => prev.filter(c => c.id !== card.id));
    } else if (destination === 'inventory') {
      setMy(prev => [...prev, card]);
      setParty(prev => prev.filter(c => c.id !== card.id));
    }

    if (e.target.className.includes('drop-area')) {
      e.target.classList.remove('drag-over');
    }
  };

  return (
    <div className="game-container">
      {/* 파티 영역 */}
      <div className="battle-area">
        <h2>파티 ({party.length}/5)</h2>
        <div 
          className={`drop-area ${party.length >= 5 ? 'full' : ''}`}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={(e) => handleDrop(e, 'party')}
        >
          {party.map((card, index) => (
            <div
              key={`party-${index}`}
              className="card"
              draggable
              onDragStart={(e) => handleDragStart(e, card, 'party')}
            >
              <span>{card.job} - {card.grade}</span>
              <div className="party-badge">파티</div>
            </div>
          ))}
        </div>
      </div>

      {/* 인벤토리 영역 */}
      <div className="inventory">
        <h2>보유 카드</h2>
        <div 
          className="drop-area"
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={(e) => handleDrop(e, 'inventory')}
        >
          {my.map((card, index) => (
            <div
              key={`inventory-${index}`}
              className="card"
              draggable
              onDragStart={(e) => handleDragStart(e, card, 'inventory')}
            >
              <span>{card.job} - {card.grade}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
