  var r = Math.floor(Math.random() * 100) + 1;  // 1~100 사이의 랜덤 숫자 생성
  var t = 5;	// 기본값: N Normal

  if (r <= 50) {	// 확률에 따라 등급을 결정
    t = 4; // H High
  }
  if (r <= 20) {	// 확률에 따라 등급을 결정
    t = 3; // R Rare
  }
  if (r <= 10) {	// 확률에 따라 등급을 결정
    t = 2; // S Super
  }
  if (r <= 4) {	// 확률에 따라 등급을 결정
    t = 1; // SR SuperRare
  }
  if (r === 1) {	// 1% 확률로 SSR
    t = 0; // SSR SuperSuperRare
  }

  return t;  // 등급 반환
}

// 주사위처럼 숫자 범위 내에서 랜덤 값을 반환하는 함수
function dice(s, e) {
  return Math.floor(Math.random() * (e - s + 1)) + s;
}

// Card 컴포넌트 통합
function Card({ job, grade, isFlipped, onFlip, draggable, onDragStart, onDragOver, onDrop, index }) {
  const [rotation, setRotation] = useState({ x: 0, y: 0 });
  const cardRef = useRef(null);

  const handleMouseMove = (e) => {
    if (!cardRef.current) return;

    const card = cardRef.current;
    const rect = card.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const centerX = rect.width / 2;
    const centerY = rect.height / 2;

    const rotateX = -(y - centerY) / 10;
    const rotateY = (x - centerX) / 10;

    setRotation({ x: rotateX, y: rotateY });
  };

  const handleMouseLeave = () => {
    setRotation({ x: 0, y: 0 });
  };

  return (
    <div 
      ref={cardRef}
      className={`card ${job} ${grade} ${isFlipped ? 'flipped' : ''}`}
      style={{
        transform: `perspective(1000px) rotateX(${rotation.x}deg) rotateY(${rotation.y}deg) ${isFlipped ? 'rotateY(180deg)' : ''}`,
        transition: 'transform 0.6s'
      }}
      onClick={onFlip}
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
      draggable={draggable}
      onDragStart={(e) => onDragStart && onDragStart(e, index)}
      onDragOver={(e) => onDragOver && onDragOver(e)}
      onDrop={(e) => onDrop && onDrop(e, index)}
    >
      <div className="card-front">
        <div className="card-content">
          <div className="card-title">{job}</div>
          <div className="card-grade">{grade}</div>
        </div>
      </div>
      <div className="card-back">
        <div className="card-back-design"></div>
      </div>
    </div>
  );
}

// 카드들을 묶어서 보여주는 영역
function CardArea({ children, onDragOver, onDrop, type }) {
  return (
    <div 
      id='card_area'
      className={`card-area ${type}`}
      onDragOver={(e) => onDragOver && onDragOver(e)}
      onDrop={(e) => onDrop && onDrop(e)}
    >
      {children}
    </div>
  );
}

// 전투 컴포넌트
function Battle({ playerParty, enemyParty, playerPartyName = "플레이어", enemyPartyName = "적" }) {
  const [isBattling, setIsBattling] = useState(false);
  const [battleLog, setBattleLog] = useState([]);
  const logContainerRef = useRef(null);

  // 카드의 스탯을 계산하는 함수
  const getCardStats = (card) => {
    // 등급별 기본 스탯
    const baseStats = {
      SSR: { hp: 100, atk: 25, def: 20 },
      SR: { hp: 80, atk: 20, def: 15 },
      S: { hp: 60, atk: 15, def: 12 },
      R: { hp: 50, atk: 12, def: 10 },
      H: { hp: 40, atk: 10, def: 8 },
      N: { hp: 30, atk: 8, def: 5 }
    };

    // 직업별 스탯 배율
    const jobMultipliers = {
      전사: { hp: 1.3, atk: 1.0, def: 1.2 },
      마법사: { hp: 0.8, atk: 1.4, def: 0.7 },
      궁수: { hp: 0.9, atk: 1.2, def: 0.8 },
      도적: { hp: 0.7, atk: 1.3, def: 0.6 },
      사제: { hp: 1.0, atk: 0.8, def: 1.0 }
    };

    const base = baseStats[card.grade];
    const multiplier = jobMultipliers[card.job];

    return {
      hp: Math.floor(base.hp * multiplier.hp),
      atk: Math.floor(base.atk * multiplier.atk),
      def: Math.floor(base.def * multiplier.def),
      maxHp: Math.floor(base.hp * multiplier.hp)
    };
  };

  // 로그 자동 스크롤
  useEffect(() => {
    if (logContainerRef.current) {
      logContainerRef.current.scrollTop = logContainerRef.current.scrollHeight;
    }
  }, [battleLog]);

  // 전투 시작 함수
  const startBattle = async () => {
    setIsBattling(true);
    setBattleLog([{ text: '전투 시작!', type: 'battle-start' }]);

    const playerCards = playerParty.map(card => ({
      ...card,
      ...getCardStats(card),
      isPlayer: true
    }));

    const enemyCards = enemyParty.map(card => ({
      ...card,
      ...getCardStats(card),
      isPlayer: false
    }));

    let turn = 1;
    while (playerCards.some(card => card.hp > 0) && 
           enemyCards.some(card => card.hp > 0)) {
      await new Promise(resolve => setTimeout(resolve, 1000));

      const isPlayerTurn = turn % 2 === 1;
      const attacker = isPlayerTurn ? playerCards : enemyCards;
      const defender = isPlayerTurn ? enemyCards : playerCards;

      setBattleLog(prev => [...prev, { 
        text: `${isPlayerTurn ? playerPartyName : enemyPartyName} 턴 시작!`, 
        type: 'turn-start' 
      }]);

      for (let card of attacker.filter(c => c.hp > 0)) {
        const target = defender.find(c => c.hp > 0);
        if (target) {
          const damage = Math.max(1, card.atk - target.def);
          target.hp = Math.max(0, target.hp - damage);

          setBattleLog(prev => [...prev, {
            text: `${card.job}(${card.grade})가 ${target.job}(${target.grade})에게 ${damage} 데미지를 입혔습니다! (${target.hp}/${target.maxHp})`,
            type: isPlayerTurn ? 'player-attack' : 'enemy-attack'
          }]);
        }
      }

      turn++;
    }

    const playerWon = playerCards.some(card => card.hp > 0);
    setBattleLog(prev => [...prev, { 
      text: playerWon ? `${playerPartyName}의 승리!` : `${enemyPartyName}의 승리!`,
      type: playerWon ? 'victory' : 'defeat'
    }]);
    setIsBattling(false);
  };

  return (
    <div className="battle-container">
      <div className="battle-header">
        <div className="party-name player">{playerPartyName}</div>
        <div className="vs">VS</div>
        <div className="party-name enemy">{enemyPartyName}</div>
      </div>
      
      <button 
        onClick={startBattle} 
        disabled={isBattling}
        className={`battle-button ${isBattling ? 'disabled' : ''}`}
      >
        {isBattling ? '전투 중...' : '전투 시작'}
      </button>

      <div 
        ref={logContainerRef} 
        className="battle-log"
      >
        {battleLog.map((log, index) => (
          <div key={index} className={`log-entry ${log.type}`}>
            {log.text}
          </div>
        ))}
      </div>
    </div>
  );
}

function App() {
  const [my, setMy] = useState([]);
  const [party, setParty] = useState([
    { job: '전사', grade: 'SSR', isFlipped: false },
    { job: '마법사', grade: 'SR', isFlipped: false },
    { job: '궁수', grade: 'S', isFlipped: false },
    { job: '전사', grade: 'R', isFlipped: false },
    { job: '궁수', grade: 'H', isFlipped: false }
  ]);
  
  // 적 파티 상태 추가 - 랜덤하게 생성
  const [enemyParty] = useState(() => {
    return Array(5).fill(null).map(() => ({
      job: jobs[dice(0, 4)],
      grade: grade[getLuck()],
      isFlipped: false
    }));
  });
  
  const [isGachaAnimating, setIsGachaAnimating] = useState(false);

  // 드래그 앤 드롭 핸들러 통합
  const handleDragStart = (e, sourceType, index) => {
    e.dataTransfer.setData('text/plain', JSON.stringify({ type: sourceType, index }));
  };

  // 드롭 처리 통합 함수
  const handleDrop = (e, targetType, targetIndex = null) => {
    e.preventDefault();
    e.stopPropagation();
    
    try {
      const { type: sourceType, index: sourceIndex } = JSON.parse(e.dataTransfer.getData('text/plain'));
      const sourceArray = sourceType === 'party' ? party : my;
      const targetArray = targetType === 'party' ? party : my;
      const setSourceArray = sourceType === 'party' ? setParty : setMy;
      const setTargetArray = targetType === 'party' ? setParty : setMy;

      // 파티 최대 인원 체크
      if (targetType === 'party' && targetArray.length >= 5 && sourceType !== 'party') {
        alert('파티 인원이 최대입니.');
        return;
      }

      if (targetIndex !== null) {
        // 카드 간 이동
        if (sourceType === targetType) {
          // 같은 영역 내 이동
          const newArray = [...sourceArray];
          const [movedCard] = newArray.splice(sourceIndex, 1);
          newArray.splice(targetIndex, 0, movedCard);
          setSourceArray(newArray);
        } else {
          // 다른 영역 간 이동
          const newSourceArray = [...sourceArray];
          const newTargetArray = [...targetArray];
          const [movedCard] = newSourceArray.splice(sourceIndex, 1);
          const [replacedCard] = newTargetArray.splice(targetIndex, 1, movedCard);
          
          if (replacedCard) {
            newSourceArray.splice(sourceIndex, 0, replacedCard);
          }
          
          setSourceArray(newSourceArray);
          setTargetArray(newTargetArray);
        }
      } else {
        // 영역에 드롭
        const newSourceArray = [...sourceArray];
        const [movedCard] = newSourceArray.splice(sourceIndex, 1);
        setSourceArray(newSourceArray);
        setTargetArray(prev => [...prev, movedCard]);
      }
    } catch (error) {
      console.error('드래그 앤 드롭 처리 중 오류:', error);
    }
  };

  // 가챠 함수 통합
  const handleGacha = async (useApi = false) => {
    setIsGachaAnimating(true);
    
    try {
      let newCard;
      if (useApi) {
        const response = await axios.get('http://localhost:8080/spring/api/gacha');
        newCard = response.data;
      } else {
        newCard = {
          job: jobs[dice(0, 4)],
          grade: grade[getLuck()]
        };
      }
      
      newCard.isFlipped = true;
      setMy(prev => [...prev, newCard]);
      
      setTimeout(() => {
        setMy(prev => prev.map((card, idx) => 
          idx === prev.length - 1 ? {...card, isFlipped: false} : card
        ));
      }, 500);
      
    } catch (error) {
      console.error('가챠 실패:', error);
      alert('카드 기에 실패했습니. 다시 시도해주세요.');
    } finally {
      setIsGachaAnimating(false);
    }
  };

  // 카드 렌더링 함수
  const renderCards = (cards, type) => {
    return cards.map((card, index) => (
      <Card 
        key={`${type}-${index}`}
        {...card}
        onFlip={() => {
          const setFunction = type === 'party' ? setParty : setMy;
          setFunction(prev => prev.map((c, idx) => 
            idx === index ? {...c, isFlipped: !c.isFlipped} : c
          ));
        }}
        draggable={true}
        onDragStart={(e) => handleDragStart(e, type, index)}
        onDragOver={(e) => e.preventDefault()}
        onDrop={(e) => handleDrop(e, type, index)}
        index={index}
      />
    ));
  };

  // 같은 직업과 등급의 카드를 그룹화하는 함수
  const groupCards = (cards) => {
    return cards.reduce((acc, card) => {
      const key = `${card.job}-${card.grade}`;
      if (!acc[key]) {
        acc[key] = { ...card, count: 1 };
      } else {
        acc[key].count++;
      }
      return acc;
    }, {});
  };

  // 인벤토리 영역의 카드 렌더링 수정
  const renderGroupedCards = () => {
    const groupedCards = groupCards(my);
    return Object.values(groupedCards).map((card, index) => (
      <div key={`grouped-${index}`} className="grouped-card">
        <Card 
          job={card.job} 
          grade={card.grade}
          isFlipped={card.isFlipped}
          onFlip={() => {
            setMy(prev => prev.map((c, idx) => 
              idx === index ? {...c, isFlipped: !c.isFlipped} : c
            ));
          }}
          draggable={true}
          onDragStart={(e) => handleDragStart(e, 'my', index)}
        />
        {card.count > 1 && (
          <div className="card-count" onClick={(e) => e.stopPropagation()}>
            x{card.count}
          </div>
        )}
      </div>
    ));
  };

  return (
    <div className="game-container">
      <div className="battle-area">
        <h2>파티 1</h2>
        <CardArea 
          onDragOver={(e) => e.preventDefault()}
          onDrop={(e) => handleDrop(e, 'party')}
          type="party"
        >
          {renderCards(party, 'party')}
        </CardArea>
      </div>

      <div className="gacha-area">
        <h2>가챠</h2>
        <button 
          onClick={() => handleGacha(false)} 
          disabled={isGachaAnimating}
          className={`gacha-button ${isGachaAnimating ? 'animating' : ''}`}
        >
          로컬 가챠
        </button>
        <button 
          onClick={() => handleGacha(true)} 
          disabled={isGachaAnimating}
          className={`gacha-button ${isGachaAnimating ? 'animating' : ''}`}
        >
          API 가챠
        </button>
      </div>

      <div className="inventory">
        <h2>보유 카드</h2>
        <CardArea>
          {renderGroupedCards()}
        </CardArea>
      </div>

      <div className="battle-section">
        <h2>전투</h2>
        <Battle playerParty={party} enemyParty={enemyParty} />
      </div>
    </div>
  );
}

export default App;
