import React, { useState } from 'react';
import './App.css';

// 이미지 import
import 전사이미지 from './images/전사.webp';
import 마법사이미지 from './images/마법사.webp';
import 궁수이미지 from './images/궁수.webp';
import 도적이미지 from './images/도적.webp';
import 사제이미지 from './images/사제.webp';

// 직업별 이미지 매핑
const jobImages = {
  전사: 전사이미지,
  마법사: 마법사이미지,
  궁수: 궁수이미지,
  도적: 도적이미지,
  사제: 사제이미지
};

function App() {
  const [my, setMy] = useState([]);
  const [party, setParty] = useState([]);
  const [draggedCard, setDraggedCard] = useState(null);

  // 카드 컴포넌트
  const Card = ({ job, grade }) => {
    return (
      <div 
        className={`card ${job} ${grade}`} 
        style={{ backgroundImage: `url(${jobImages[job]})` }}
      >
        <span>{job} - {grade}</span>
      </div>
    );
  };

  // 드래그 시작
  const handleDragStart = (card, sourceArea) => (e) => {
    const dragInfo = {
      card: { ...card },
      sourceArea: sourceArea
    };
    setDraggedCard(dragInfo);
    e.currentTarget.classList.add('dragging');
  };

  // 나머지 코드는 그대로 유지...

  return (
    <div className="game-container">
      {/* 기존 JSX 코드에서 Card 컴포넌트 사용 */}
      <div className="battle-area">
        <h2>파티 ({party.length}/5)</h2>
        <div className="drop-area">
          {party.map((card, index) => (
            <Card 
              key={`party-${index}`}
              job={card.job}
              grade={card.grade}
            />
          ))}
        </div>
      </div>

      <div className="inventory">
        <h2>보유 카드</h2>
        <div className="drop-area">
          {my.map((card, index) => (
            <Card 
              key={`inventory-${index}`}
              job={card.job}
              grade={card.grade}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
